mapper.update:
UPDATE ttest
	SET
		content = #{content}
	WHERE testId = #{testId}

mapper.insert:
INSERT INTO ttest(testId, content)
VALUES (#{testId},#{content})
ON DUPLICATE KEY UPDATE content = #{content}

mapper.select:
SELECT USERID, USERPASS
FROM mp_member
WHERE USERID =#{userId}
AND USERPASS= #{userPass}
